    //  xóa dấu tiếng Việt
    function vn_to_str ($str){
        $unicode = array(
            'a'=>'á|à|ả|ã|ạ|ă|ắ|ặ|ằ|ẳ|ẵ|â|ấ|ầ|ẩ|ẫ|ậ',
            'd'=>'đ',
            'e'=>'é|è|ẻ|ẽ|ẹ|ê|ế|ề|ể|ễ|ệ',
            'i'=>'í|ì|ỉ|ĩ|ị',
            'o'=>'ó|ò|ỏ|õ|ọ|ô|ố|ồ|ổ|ỗ|ộ|ơ|ớ|ờ|ở|ỡ|ợ',
            'u'=>'ú|ù|ủ|ũ|ụ|ư|ứ|ừ|ử|ữ|ự',
            'y'=>'ý|ỳ|ỷ|ỹ|ỵ',
            'A'=>'Á|À|Ả|Ã|Ạ|Ă|Ắ|Ặ|Ằ|Ẳ|Ẵ|Â|Ấ|Ầ|Ẩ|Ẫ|Ậ',
            'D'=>'Đ',
            'E'=>'É|È|Ẻ|Ẽ|Ẹ|Ê|Ế|Ề|Ể|Ễ|Ệ',
            'I'=>'Í|Ì|Ỉ|Ĩ|Ị',
            'O'=>'Ó|Ò|Ỏ|Õ|Ọ|Ô|Ố|Ồ|Ổ|Ỗ|Ộ|Ơ|Ớ|Ờ|Ở|Ỡ|Ợ',
            'U'=>'Ú|Ù|Ủ|Ũ|Ụ|Ư|Ứ|Ừ|Ử|Ữ|Ự',
            'Y'=>'Ý|Ỳ|Ỷ|Ỹ|Ỵ',
        );
        foreach($unicode as $nonUnicode=>$uni){
            $str = preg_replace("/($uni)/i", $nonUnicode, $str);
        }
        return $str;
    }
    $enClass = array();
    foreach ($classes as $c) { 
        array_push($enClass, vn_to_str($c));
    }

    // chưa hiểu thì hỏi :)

    $keyword = $_POST['keyword'];
    $keyword = strtolower($keyword);

    $filter = array();
    $id = array();



    // tìm xem khớp classes ở vị trí nào thì chèn vào $filter + chèn luôn id ở vị trí đó vào $id
    for($i = 0; $i < count($classes); ++$i) {
        if (preg_match("/$keyword/", strtolower($enClass[$i])) or preg_match("/$keyword/", strtolower($classes[$i]))){ // regular expression
            array_push($filter, $classes[$i]);
            array_push($id, $idClasses[$i]);
        }
    }
      

    // foreach ($classes as $c){
    //         if (preg_match("/$keyword/", strtolower($c))){ // regular expression
    //             array_push($filter, $c);
    //         }
    //     }
    // }

    $response = array('success'=>true, 'class'=>$filter, 'idClass'=>$id);
    echo json_encode($response);
